INSERT INTO `generator_template` (`id`, `name`, `key`, `enable_package`, `target_package`, `target_path`, `template_content`, `scheme_id`, `name_format`, `name_underline`, `sort`) VALUES ('237a0c53552f4be28e49fd8467d0ddc7', 'js文件', 'js', '1', 'js', 'E:\\testgen', 'layui.extend({\r\n    serializejson: \'../js/serializejson\',\r\n    dealSearch: \'../js/common\',\r\n	underscore: \'../js/underscore\'\r\n}).use([\'element\', \'form\', \'table\', \'laytpl\', \'layer\', \'dealSearch\', \'underscore\'], function () {\r\n    var $ = layui.jquery,\r\n        element = layui.element,\r\n        form = layui.form,\r\n        table = layui.table,\r\n        layer = layui.layer,\r\n        laytpl = layui.laytpl,\r\n        dealSearch = layui.dealSearch,\r\n        _ = layui.underscore;\r\n\r\n    var tableIndex = table.render({\r\n        elem: \'#${entityName?uncap_first}List\',\r\n        height: \'full-50\', //容器高度\r\n        url: \'ajaxList\',\r\n        page: {\r\n            limit: 10,\r\n            limits: [10, 20, 50]\r\n        },\r\n        request: {\r\n            limitName: \'size\',\r\n            pageName: \'current\'\r\n        },\r\n        response: {\r\n            countName: \'total\'\r\n        },\r\n        id: \'${entityName?uncap_first}List\',\r\n        cols: [\r\n            [{\r\n                type: \'checkbox\',\r\n                fixed: true\r\n            },\r\n             &lt;#list columns as column&gt;\r\n				{\r\n                    field: \'${column.javaField}\',\r\n                    title: \'${column.javaField}\',\r\n                    width: 100,\r\n                    sort: true\r\n                },\r\n             &lt;/#list&gt;\r\n                {\r\n                    fixed: \'right\',\r\n                    title: \'操作\',\r\n                    align: \'center\',\r\n                    toolbar: \'#${entityName?uncap_first}Bar\',\r\n                    width: 200\r\n                }\r\n            ]\r\n        ],\r\n        done