                                layer.msg(res.msg, {\r\n                                                icon: 1,\r\n                                                time: 2000\r\n                                            }, function () {\r\n                                                tableIndex.reload();\r\n                                            });\r\n                                        } else {\r\n                                            $(\'form[lay-filter=&quot;${entityName?uncap_first}Form&quot;]\').find(\'button[lay-submit]\').prop(\'disabled\', false);\r\n                                            layer.alert(res.msg, {\r\n                                                icon: 2\r\n                                            });\r\n                                        }\r\n                                    });\r\n                                }\r\n                            });\r\n                            return false;\r\n                        });\r\n                    },\r\n                    btn2: function () {\r\n                        $(\'form[lay-filter=&quot;${entityName?uncap_first}Form&quot;]\').find(\'button[lay-submit]\').click();\r\n                        return false;\r\n                    }\r\n                })\r\n            });\r\n        });\r\n    });\r\n\r\n    //监听工具条\r\n    table.on(\'tool(${entityName?uncap_first}List)\', function (obj) {\r\n        var data = obj.data;\r\n        var layEvent = obj.event;\r\n        if (layEvent === \'edit\') { //编辑\r\n          	var loadingLayer = layer.load(2, { shade: 0.3 });\r\n            $.get(data.${entityName?uncap_first}Id + \'/view\')\r\n                .done(function (d) {\r\n              		layer.close(loadingLayer);\r\n                    laytpl($(\'#${entityName?uncap_first}FormTpl\').html()).render(d.data, function (html) {\r\n                        layer.open({\r\n                            type: 1,\r\n                            title: \'编辑\',\r\n                            content: html,\r\n                            area: \'800px\',\r\n                            btn: [\'取消\', \'确定\'],\r\n                            maxHeight: $(window).height() - 20,\r\n                            success: function () {\r\n                                form.render(null, \'${entityName?uncap_first}Form\');\r\n                                form.on(\'submit(${entityName?uncap_first}Submit)\', function (data) {\r\n                                    layer.confirm(\'确定提交？\', {\r\n                                        icon: 3,\r\n                                        title: \'提示\',\r\n                                        btn: [\'取消\', \'确认\'],\r\n                                        yes: function (i) {\r\n                                            layer.close(i);\r\n                                        },\r\n                                        btn2: function () {\r\n                                            $(\'form[lay-filter=&quot;${entityName?uncap_first}Form&quot;]\').find(\'button[lay-submit]\').prop(\'disabled\', true);\r\n                                            loadingLayer = layer.load(2, { shade: 0.3 });\r\n                                            $.post(\'update\', data.field, function (res) {\r\n                                                layer.close(loadingLayer);\r\n                                                if (res.code === 0) {\r\n                                                    layer.closeAll();\r\n                                                    layer.msg(res.msg, {\r\n                                                        icon: 1,\r\n                                                        time: 2000\r\n                                                    }, function () {\r\n                                                        tableIndex.reload();\r\n                                                    });\r\n                                                } else {\r\n                                                    $(\'form[lay-filter=&quot;${entityName?uncap_first}Form&quot;]\').find(\'button[lay-submit]\').prop(\'disabled\', false);\r\n                                                    layer.alert(res.msg, {\r\n                                                        icon: 2\r\n                                                    });\r\n                                                }\r\n                                            });\r\n                                        }\r\n                                    });\r\n                                    return false;\r\n                                });\r\n                            },\r\n                            btn2: function () {\r\n                                $(\'form[lay-filter=&quot;${entityName?uncap_first}Form&quot;]\').find(\'button[lay-submit]\').click();\r\n                                return false;\r\n                            }\r\n                        })\r\n                    });\r\n                });\r\n        } else if (layEvent === \'del\') { //删除\r\n            layer.confirm(\'确定删除该数据？\', {\r\n                icon: 3,\r\n                title: \'提示\',\r\n                btn: [\'取消\', \'确认\']\r\n            }, function (index) {\r\n                layer.close(index);\r\n            }, function () {\r\n              	var loadingLayer = layer.load(2, {shade: 0.3});\r\n                $.post(data.${entityName?uncap_first}Id + \'/delete\')\r\n                    .done(function (res) {\r\n                  		loadingLayer = layer.load(2, {shade: 0.3});\r\n                        if (res.code === 0) {\r\n                            layer.closeAll();\r\n                            layer.msg(res.msg, {\r\n                                icon: 1,\r\n                                time: 2000\r\n                            }, function () {\r\n                                tableIndex.reload();\r\n                            });\r\n                        } else {\r\n                            layer.alert(res.msg, {\r\n                                icon: 2\r\n                            });\r\n                        }\r\n                    });\r\n            });\r\n\r\n        } else if (layEvent === \'view\') { //查看\r\n            $.get(data.${entityName?uncap_first}Id + \'/view\')\r\n                .done(function (d) {\r\n                    laytpl($(\'#${entityName?uncap_first}FormTpl\').html()).render(d.data, function (html) {\r\n                        layer.open({\r\n                            type: 1,\r\n                            title: \'详情\',\r\n                            content: html,\r\n                            area: [\'800px\', $(window).height() - 20 + \'px\'],\r\n                            success: function () {\r\n                                $(\'#${entityName?uncap_first}Form\').find(\'input,select\').attr(\'readonly\', \'readonly\');\r\n                                form.render(null, \'${entityName?uncap_first}Form\');\r\n                            }\r\n                        })\r\n                    })\r\n                })\r\n        }\r\n    });\r\n});', '67f7f85896dc4755b9f2e25888795a54', '[functionName].js', '0', 8);
INSERT INTO `generator_template` (`id`, `name`, `key`, `enable_package`, `target_package`, `target_path`, `template_content`, `scheme_id`, `name_format`, `name_underline`, `sort`) VALUES ('4d0a93a0f45f4911a1a5d21a3d6644f8', '服务接口实现', 'ServiceImpl', '1', 'com.yph.auth.service.impl', 'E:\\testgen', 'package ${targetPackage};\r\n\r\nimport ${iservice.targetPackage}.I${entityName?cap_first}Service;\r\nimport ${entity.targetPackage}.${entityName?cap_first};\r\nimport ${mapper.targetPackage}.${entityName?cap_first}Mapper;\r\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\r\nimport com.baomidou.mybatisplus.core.metadata.IPage;\r\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\r\nimport com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;\r\nimport com.yph.authcommon.ExcelUtils;\r\nimport org.slf4j.Logger;\r\nimport org.slf4j.LoggerFactory;\r\nimport org.springframework.beans.factory.annotation.Autowired;\r\nimport org.springframework.cglib.beans.BeanMap;\r\nimport org.springframework.stereotype.Service;\r\nimport org.springframework.transaction.annotation.Transactional;\r\n\r\nimport javax.servlet.http.HttpServletRequest;\r\nimport javax.servlet.http.HttpServletResponse;\r\nimport java.io.IOException;\r\nimport java.lang.reflect.Field;\r\nimport java.util.ArrayList;\r\nimport java.util.List;\r\nimport java.util.Map;\r\n\r\n\r\n/**\r\n* @package ${targetPackage}\r\n* @title: ${functionName}服务实现\r\n* @description: ${functionDesc}服务实现\r\n* @author: ${functionAuthor}\r\n* @date: ${time}\r\n*/\r\n@Transactional\r\n@Service(&quot;${entityName?lower_case}Service&quot;)\r\npublic class ${entityName?cap_first}ServiceImpl  extends ServiceImpl&lt;${entityName?cap_first}Mapper, ${entityName?cap_first}&gt; implements  I${entityName?cap_first}Service {\r\n\r\n	protected Logger LOGGER = LoggerFactory.getLogger(getClass());\r\n\r\n	@Autowired\r\n    private ${entityName?cap_first}Mapper ${functionName}Mapper;\r\n\r\n    @Override\r\n    public void export(HttpServletRequest request, HttpServletResponse response, Map&lt;String,Object&gt; queryMap)\r\n    {\r\n        try\r\n        {\r\n            //查询待导出数据\r\n            List&lt;${entityName?cap_first}&gt; resultList = baseMapper.selectAll(queryMap);\r\n\r\n            //封装导出参数\r\n            String excelName = &quot;${functionName}数据导出&quot;;//导出的EXCEL名字\r\n            String sheetName = &quot;${functionName}数据导出&quot;;//导出的SHEET名字\r\n\r\n            Class cls = ${entityName?cap_first}.class;\r\n            Field[] fields = cls.getDeclaredFields();\r\n\r\n            String[] headers = new String[fields.length];//导出的表格的表头\r\n            String[] ds_titles = new String[fields.length];//导出的数据 map.get(key) 对应的 key\r\n            int[] ds_format = new int[fields.length];//导出数据的样式\r\n\r\n            for (int i = 0; i &lt; fields.length; i++)\r\n            {\r\n                Field f = fields[i];\r\n                headers[i] = f.getName();\r\n                ds_titles[i] = f.getName();\r\n                ds_format[i] = ExcelUtils.DS_FORMAT_STRING_CENTER;\r\n            }\r\n\r\n            List&lt;Map&lt;String, Object&gt;&gt; data = new ArrayList&lt;Map&lt;String, Object&gt;&gt;();\r\n            for (${entityName?cap_first} obj : resultList)\r\n            {\r\n                //实体对象转map对象\r\n                Map&lt;String, Object&gt; map = BeanMap.create(obj);\r\n                data.add(map);\r\n            }\r\n            ExcelUtils.export(excelName, sheetName, headers, ds_titles, ds_format, null, data, request, response);\r\n        } catch (IOException e)\r\n        {\r\n            LOGGER.error(&quot;导出excel出错&quot;, e);\r\n        }\r\n    }\r\n    \r\n    @Override\r\n    public IPage&lt;${entityName?cap_first}&gt; selectAll(Page&lt;${entityName?cap_first}&gt; page, Map&lt;String, Object&gt; queryMap) {\r\n        return ${functionName}Mapper.selectAll(page,queryMap);\r\n    }\r\n}', '67f7f85896dc4755b9f2e25888795a54', '[entityName]ServiceImpl.java', '1', 3);
INSERT INTO `generator_template` (`id`, `name`, `key`, `enable_package`, `target_package`, `target_path`, `template_content`, `scheme_id`, `name_format`, `name_underline`, `sort`) VALUES ('74f91057a36f4a279b2d838582e8e6a0', '服务接口', 'iservice', '1', 'com.yph.auth.service', 'E:\\testgen', 'package ${targetPackage};\r\n\r\nimport com.baomidou.mybatisplus.core.metadata.IPage;\r\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\r\nimport com.baomidou.mybatisplus.extension.service.IService;\r\nimport ${entity.targetPackage}.${entityName?cap_first};\r\nimport java.util.Map;\r\nimport javax.servlet.http.HttpServletRequest;\r\nimport javax.servlet.http.HttpServletResponse;\r\n\r\n/**\r\n* @package ${targetPackage}\r\n* @title: ${functionName}服务接口\r\n* @description: ${functionDesc}服务接口\r\n* @author: ${functionAuthor}\r\n* @date: ${time}\r\n*/\r\npublic interface I${entityName?cap_first}Service extends IService&lt;${entityName?cap_first}&gt; {\r\n\r\n    /**\r\n     * 导出excel\r\n     *\r\n     * @author ${functionAuthor}\r\n     * @date ${time}\r\n     * @param [wrapper]\r\n     * @return void\r\n     */\r\n    void export(HttpServletRequest request, HttpServletResponse response,Map&lt;String,Object&gt; queryMap);\r\n    /**  \r\n     * 分页查询\r\n     *   \r\n     * @author ${functionAuthor}\r\n     * @date ${time}\r\n     * @param [page,queryMap]\r\n     * @return com.baomidou.mybatisplus.core.metadata.IPage&lt;${entityName?cap_first}&gt;  \r\n     */ \r\n    IPage&lt;${entityName?cap_first}&gt; selectAll(Page&lt;${entityName?cap_first}&gt; page, Map&lt;String,Object&gt; queryMap);\r\n}', '67f7f85896dc4755b9f2e25888795a54', 'I[entityName]Service.java', '1', 2);
INSERT INTO `generator_template` (`id`, `name`, `key`, `enable_package`, `target_package`, `target_path`, `template_content`, `scheme_id`, `name_format`, `name_underline`, `sort`) VALUES ('772be5a386574378ae2f11f7b4d10671', '视图查看模版', 'view', '1', '[functionName]', 'E:\\testgen', '&lt;script type=&quot;text/html&quot; id=&quot;${entityName?uncap_first}FormTpl&quot;&gt;\r\n    &lt;div style=&quot;margin: 10px;&quot;&gt;\r\n        &lt;form class=&quot;layui-form&quot; id=&quot;${entityName?uncap_first}Form&quot; lay-filter=&quot;${entityName?uncap_first}Form&quot;&gt;\r\n            &lt;input type=&quot;hidden&quot; name=&quot;${entityName?uncap_first}Id&quot; value=&quot;{{d.${entityName?uncap_first}Id || \'\'}}&quot;&gt;\r\n            &lt;#assign index=1 /&gt;\r\n            &lt;#list columns as column&gt;\r\n                &lt;#if column.formable?? &amp;&amp; column.formable&gt;\r\n                    &lt;#assign index=index+1 /&gt;\r\n                    &lt;#if index%2==0&gt; \r\n                    &lt;div class=&quot;layui-form-item&quot;&gt;  \r\n                    &lt;/#if&gt;\r\n                    	&lt;#if column.javaField != \'${entityName?uncap_first}Id\' &gt;\r\n                        &lt;div class=&quot;layui-inline&quot;&gt;\r\n                            &lt;label class=&quot;layui-form-label&quot;&gt;${column.javaField}&lt;/label&gt;\r\n                            &lt;div class=&quot;layui-input-inline&quot;&gt;\r\n                                &lt;input type=&quot;text&quot; class=&quot;layui-input&quot; value=&quot;{{d.${column.javaField} || \'\'}}&quot; name=&quot;${column.javaField}&quot; readonly&gt;\r\n                            &lt;/div&gt;\r\n                        &lt;/div&gt;\r\n                        &lt;/#if&gt;\r\n                    &lt;#if index%2==1&gt; \r\n                    &lt;/div&gt;\r\n                    &lt;#elseif index==(columns?size+1) &gt;\r\n                    &lt;/div&gt;\r\n                    &lt;/#if&gt;\r\n                &lt;/#if&gt;\r\n            &lt;/#list&gt;\r\n        &lt;/form&gt;\r\n    &lt;/div&gt;\r\n&lt;/script&gt;', '67f7f85896dc4755b9f2e25888795a54', 'view.html', '1', 9);
